import React, { Component } from 'react';
import './App.css';
import { API_KEY } from './api/Key'

import Search from './components/nav/Search'
import Today from './components/weather-card/Today'
import Week from './components/weather-card/Week'


import { library } from '@fortawesome/fontawesome-svg-core'
import { faSearch, faFrown,faTint,faWind, faLocationArrow, faSun,faCircle} from '@fortawesome/free-solid-svg-icons'
library.add(faSearch,faFrown,faTint,faWind, faLocationArrow, faSun,faCircle)


class App extends Component {

  getLocation = async (e) =>{
    this.watchId = navigator.geolocation.getCurrentPosition(
      position => {
        this.setState({
          latitude: position.coords.latitude,
          longitude: position.coords.longitude,
          error: null
        },
        ()=>{
          var lat = `${this.state.latitude}`;
          var lon = `${this.state.longitude}`;
          let API_WEATHER = `http://api.openweathermap.org/data/2.5/forecast?lat=${lat}&lon=${lon}&units=metric&appid=${API_KEY}`;
          fetch(API_WEATHER)
  .then(response => response.json())
  .then(responseJson => {
        console.log(responseJson);
        console.log(responseJson.forecast);
        this.setState({
          city: responseJson.city.name,
          country: responseJson.city.country,
          temperature: Math.floor(responseJson.list["0"].main.temp).toFixed(0),
          temperature1h: Math.floor(responseJson.list["1"].main.temp),
          temperature2h: Math.floor(responseJson.list["2"].main.temp),
          temperature3h: Math.floor(responseJson.list["3"].main.temp),
          time: responseJson.list["0"].dt,
          time1: responseJson.list["1"].dt,
          time2: responseJson.list["2"].dt,
          time3: responseJson.list["3"].dt,
          icon: responseJson.list["0"].weather["0"].id,
          icon1h: responseJson.list["1"].weather["0"].id,
          icon2h: responseJson.list["2"].weather["0"].id,
          icon3h: responseJson.list["3"].weather["0"].id,
          description: responseJson.list[3].weather.description,
          humidity: responseJson.list[0].main.humidity,
          humidity1h: responseJson.list[1].main.humidity,
          humidity2h: responseJson.list[2].main.humidity,
          humidity3h: responseJson.list[4].main.humidity,
          wind: responseJson.list[0].wind.speed,
          wind1h: responseJson.list[1].wind.speed,
          wind2h: responseJson.list[2].wind.speed,
          wind3h: responseJson.list[4].wind.speed,
          night: responseJson.list["0"].sys.pod,
          // sunrise: responseJson.sys.sunrise,
          // sunset: responseJson.sys.sunset,

          temperature1d: Math.floor(responseJson.list["6"].main.temp),
          temperature2d: Math.floor(responseJson.list["14"].main.temp),
          temperature3d: Math.floor(responseJson.list["22"].main.temp).toFixed(0),
          temperature4d: Math.floor(responseJson.list["30"].main.temp),
          temperature5d: Math.floor(responseJson.list["38"].main.temp),
          icon1d: responseJson.list["6"].weather["0"].id,
          icon2d: responseJson.list["14"].weather["0"].id,
          icon3d: responseJson.list["22"].weather["0"].id,
          icon4d: responseJson.list["30"].weather["0"].id,
          icon5d: responseJson.list["38"].weather["0"].id,

          error: ""
        });
      })


      .catch(error => {
        console.log(error);
      });

    });
    },


    error => this.setState({ error: error.message }),
    {
      enableHighAccuracy: true,
      timeout: 20000,
      maximumAge: 1000,
      distanceFilter: 5
    }
  );
}

  render() {
    return (
      <div>
        <Search />
        <Today />
        <Week />
      </div>
    );
  }
}

export default App;
