import React from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const Today = props => (

    <div className="weather-content">

        <div className="flex-one">
            <div className="weather-item">
                <p className="weather-value time">1:00pm</p>
                <p className="weather-value city">Stockholm</p>
                <p className="weather-value country">SE</p>
            </div>
            <div className="weather-item">
            <h2 className="weather-value temp">-3<span className="celsius">Â°C</span></h2>
            </div>
        </div>

        <div className="flex-two">
            <div className="weather-item">
                <div className="weather-value icon">
                <FontAwesomeIcon className="icon-hum-wi" icon="sun" />{" "}
                </div>
            </div>
            <div className="weather-item">
                <p className="weather-value desc">sunny</p>
            </div>
        </div>

        <div className="flex-three">
    {props.sunrise && (
        <div className="weather-item-small">
          <FontAwesomeIcon className="icon-hum-wi" icon="sun" />{" "}
          <div className="wind"><Timestamp utc={false} time={ props.sunrise } format='time' /></div>
        </div>
     )}
     {props.wind && (
        <div className="weather-item-small">
          <FontAwesomeIcon className="icon-hum-wi" icon="wind" />{" "}
          <div className="wind">{props.wind}m/s</div>
        </div>
 )}
 {props.humidity && (
        <div className="weather-item-small">
          <FontAwesomeIcon className="icon-hum-wi" icon="tint" />
          <div className="wind">{props.humidity}% </div>
        </div>
)}
{props.sunset && (
        <div className="weather-item-small">
          <FontAwesomeIcon className="icon-hum-wi" icon="sun" />
          <div className="wind"> <Timestamp utc={false} time={ props.sunset } format='time' /></div>
        </div>
       )}
    </div>


    </div>
)

export default Today;
